import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { ApiService } from '../../services/api.service';
import { CartService } from '../../services/cart.service';

@Component({
    selector: 'app-checkout',
    templateUrl: './checkout.component.html',
    styleUrls: ['./checkout.component.css']
})
export class CheckoutComponent implements OnInit {
    userId = 1; // Hardcoded user ID for demo purposes (assuming user is logged in)
    cartItems: any[] = [];
    total = 0;

    constructor(private apiService: ApiService, private router: Router, private cartService: CartService) { }

    ngOnInit(): void {
        this.cartItems = this.cartService.getCartItems();
        this.calculateTotal();
    }

    calculateTotal() {
        this.total = this.cartService.getTotal();
    }

    updateQuantity(item: any, event: any) {
        const newQuantity = parseInt(event.target.value, 10);
        if (newQuantity > 0) {
            this.cartService.updateQuantity(item.id, newQuantity);
        } else {
            this.removeFromCart(item);
        }
        this.calculateTotal();
    }

    increaseQuantity(item: any) {
        this.cartService.updateQuantity(item.id, item.quantity + 1);
        this.calculateTotal();
    }

    decreaseQuantity(item: any) {
        if (item.quantity > 1) {
            this.cartService.updateQuantity(item.id, item.quantity - 1);
        } else {
            this.removeFromCart(item);
        }
        this.calculateTotal();
    }

    removeFromCart(item: any) {
        if (confirm('¿Estás segura de que quieres eliminar este artículo?')) {
            this.cartService.removeFromCart(item.id);
            this.cartItems = this.cartService.getCartItems(); // Refresh local list
            this.calculateTotal();
        }
    }

    onSubmit() {
        const orderData = {
            userId: this.userId,
            items: this.cartItems.map(item => ({
                productId: item.id,
                quantity: item.quantity,
                price: item.price
            })),
            total: this.total
        };


        this.apiService.createOrder(orderData).subscribe(
            (response) => {
                alert('¡Pedido realizado con éxito! ID: ' + response.orderId);
                this.cartItems = [];
                this.total = 0;
                this.router.navigate(['/']);
            },
            (error) => {
                console.error(error);
                alert('Error al procesar el pedido.');
            }
        );
    }
}
