import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ApiService } from '../../services/api.service';

@Component({
    selector: 'app-product-detail',
    templateUrl: './product-detail.component.html',
    styleUrls: ['./product-detail.component.css']
})
export class ProductDetailComponent implements OnInit {
    productId: number | null = null;
    product: any = null;

    constructor(private route: ActivatedRoute, private apiService: ApiService) { }

    ngOnInit(): void {
        this.productId = Number(this.route.snapshot.paramMap.get('id'));
        // Fetch all products and find the matching one (simple demo implementation)
        this.apiService.getProducts().subscribe(
            (products) => {
                this.product = products.find((p: any) => p.id === this.productId);
            },
            (error) => {
                console.error('Error fetching product details:', error);
            }
        );
    }
}

this.productId = Number(this.route.snapshot.paramMap.get('id'));
// In a real app, we would have a getProductById endpoint. 
// For now, we'll fetch all and find (inefficient but meets strict requirements without adding unrequested endpoints).
this.apiService.getProducts().subscribe(products => {
    this.product = products.find((p: any) => p.id === this.productId);
});
}
}
