import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { ApiService } from '../../services/api.service';

@Component({
    selector: 'app-checkout',
    templateUrl: './checkout.component.html',
    styleUrls: ['./checkout.component.css']
})
export class CheckoutComponent implements OnInit {
    userId = 1; // Hardcoded user ID for demo purposes (assuming user is logged in)
    cartItems: any[] = [];
    total = 0;

    constructor(private apiService: ApiService, private router: Router) { }

    ngOnInit(): void {
        // Simulate fetching cart items
        this.cartItems = [
            { productId: 1, name: 'Vestido de Noche Rojo', price: 120.00, quantity: 1 },
            { productId: 2, name: 'Vestido de Verano Floral', price: 45.50, quantity: 2 }
        ];
        this.calculateTotal();
    }

    calculateTotal() {
        this.total = this.cartItems.reduce((acc, item) => acc + (item.price * item.quantity), 0);
    }

    onSubmit() {
        const orderData = {
            userId: this.userId,
            items: this.cartItems,
            total: this.total
        };

        this.apiService.createOrder(orderData).subscribe(
            (response) => {
                alert('¡Pedido realizado con éxito! ID: ' + response.orderId);
                this.cartItems = [];
                this.total = 0;
                this.router.navigate(['/']);
            },
            (error) => {
                console.error(error);
                alert('Error al procesar el pedido.');
            }
        );
    }
}
